        ns = 10e6
    }
    for atomic.Load(key32(&n.key)) == 0 { // --------here
        gp.m.blocked = true
        futexsleep(key32(&n.key), 0, ns) //进入睡眠
        if *cgo_yield != nil {
            asmcgocall(*cgo_yield, nil)
        }
        gp.m.blocked = false
    }
}


