func (wg *WaitGroup) Add(delta int) {
    statep, semap := wg.state()
    if race.Enabled {
        _ = *statep // trigger nil deref early
        if delta > 32)
    w := uint32(state)
    if race.Enabled && delta > 0 && v == int32(delta) {
